'use strict';

var React = require('react');
var createClass = require('create-react-class');
var PropTypes = require('prop-types');

module.exports = createClass({
  displayName: 'Arrow',

  propTypes: {
    direction: PropTypes.string,
    show: PropTypes.bool.isRequired,
    onClick: PropTypes.func,
    top: PropTypes.number,
    left: PropTypes.number,
    relative: PropTypes.bool
  },

  render: function render() {
    var direction = this.props.direction || 'left';
    var className = 'horizontal-scrolling-' + direction;
    var top = this.props.top || 0;
    var left = this.props.left || 0;
    var position = this.props.relative ? 'absolute' : 'fixed';
    var style = { top: top, left: left, position: position };

    return this.props.show ? React.createElement('div', {
      className: className,
      onClick: this.props.onClick,
      style: style }) : null;
  }
});